import React, { useState } from 'react';
import { createSearchParams, Link, useNavigate } from 'react-router-dom';
import '../w3.css';
import NavBar from './NavBar';
import Modal from 'react-modal';

// Entity component
const User = ({ username, password, status, acctype, loyaltypoint, seatpref, id }) => {
  return {
    username,
    password,
    status,
    acctype,
    loyaltypoint,
    seatpref,
    id,
  };
};

// Controller component
const UserController = () => {
  const [modalIsOpen4, setModalIsOpen4] = useState(false);
  const history = useNavigate();

  const handleSubmit = (e, username, password) => {
    e.preventDefault();
    fetch('http://localhost:8005/user')
      .then(res => {
        return res.json();
      })
      .then(data => {
        console.log(data);
        const user = data.find(
          record => record.username === username && record.password === password && record.status === 'Active'
        );
        if (user) {
          const { acctype, loyaltypoint, seatpref, id } = user;
          if (acctype === 'customer') {
            console.log('redirect to customer page');
            history('/user', { state: { username, loyaltypoint, seatpref, id } });
          } else if (acctype === 'staff') {
            console.log('redirect to staff page');
            history('/stafffoodanddrink', { state: { username } });
          } else if (acctype === 'Manager') {
            console.log('redirect to manager page');
            history('/manager', { state: { username } });
          } else if (acctype === 'System Admin') {
            console.log('redirect to admin page');
            history('/admin', { state: { username } });
          }
        } else {
          setModalIsOpen4(true);
        }
      });
  };

  return {
    handleSubmit,
    modalIsOpen4,
    setModalIsOpen4,
  };
};

const customStyles = {
    content: {
      top: '50%',
      left: '50%',
      right: 'auto',
      bottom: 'auto',
      marginRight: '-50%',
      transform: 'translate(-50%, -50%)',
      backgroundColor: '#4f4747'
    }
  };
  
  const LoginBoundary = ({ onLogin }) => {
    const [username, setUsername] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [modalIsOpen4, setModalIsOpen4] = React.useState(false);
  
    const handleSubmit = (e) => {
      e.preventDefault();
      onLogin(username, password);
    };
  
    const closeModal = () => {
      setModalIsOpen4(false);
    };
  
    return (
      <>
        {/* <!-- Page content --> */}
        <div className="w3-content w3-padding w3-center" style={{ width: '60%' }}>
          {/* <!-- Log in Section --> */}
          <div className="w3-container w3-margin-top" id="login">
            <div className="w3-padding-32">
              <h1 className="w3-border-bottom w3-border-teal w3-padding-64">Sign In</h1>
              <form onSubmit={handleSubmit} className="w3-padding-32">
                <input
                  value={username}
                  placeholder="Username"
                  onChange={(e) => setUsername(e.target.value)}
                  className="w3-input w3-section w3-border w3-round"
                  type="text"
                  required
                  name="Username"
                />
                <input
                  type="password"
                  value={password}
                  placeholder="Password"
                  onChange={(e) => setPassword(e.target.value)}
                  className="w3-input w3-section w3-border w3-round"
                  required
                  name="Password"
                />
                <div className="w3-center w3-padding-large w3-padding-32">
                  <button className="w3-button w3-teal w3-round-large w3-margin-top">SIGN IN</button>
                  <Link to="/createaccount" className="w3-button w3-light-grey w3-round-large w3-margin-top">
                    CREATE A NEW ACCOUNT
                  </Link>
                </div>
              </form>
            </div>
          </div>
  
          {/* <!-- End page content --> */}
        </div>
  
        <Modal isOpen={modalIsOpen4} onRequestClose={closeModal} style={customStyles}>
          <h1>Incorrect Username Or Password</h1>
          <div className="Iwantaligncenter">
            <button className="w3-button w3-light-grey w3-round-large w3-margin-top" onClick={closeModal}>
              Close
            </button>
          </div>
        </Modal>
      </>
    );
  };
  
  export default LoginBoundary;
