import { useEffect, useState } from "react";
const SelectSeats = () => {
  const [seats, setSeats] = useState([]);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [seat, setseat] = useState();
  var listofseatselected = [];
  useEffect(() => {
    fetch("http://localhost:2000/seats")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setSeats(data);
      });
  }, []);

  const handleSubmit = (e) => {
    console.log(e.target.value);
    listofseatselected.push(e.target.value);
    console.log(listofseatselected);
  };
  const cubegrey = {
    width: "20px",
    height: "20px",
    backgroundColor: "grey",
  };
  const cubewhite = {
    width: "20px",
    height: "20px",
    backgroundColor: "white",
  };
  const cubecyan = {
    width: "20px",
    height: "20px",
    backgroundColor: "cyan",
  };
  const handleSeatClick = (seat) => {
    if (seat.occupy === "no") {
      setSeats((seats) =>
        seats.map((s) =>
          s.id === seat.id ? { ...s, selected: !s.selected } : s
        )
      );
      if (!seat.selected) {
        listofseatselected.push(seat.id);
        console.log("Selected seats: ", listofseatselected);
      }
    }
  };
  const renderSeat = (seat) => {
    let style;
    if (seat.occupy === "yes") {
      style = { backgroundColor: "white" };
    } else if (seat.selected) {
      style = { backgroundColor: "cyan" };
    } else {
      style = { backgroundColor: "grey" };
    }
    return (
      <div
        key={seat.id}
        style={{ ...style, width: "20px", height: "20px" }}
        onClick={() => handleSeatClick(seat)}
      >
        {seat.occupy === "no" && !seat.selected ? seat.seat : ""}
      </div>
    );
  };
  return (
    <div>
      {seats.map((seat) => (
        <div key={seat.id}>{renderSeat(seat)}</div>
      ))}
    </div>
  );
};

export default SelectSeats;
