import { useEffect, useState } from "react";
const SelectSeats = () => {
  const [seats, setSeats] = useState([]);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [seat, setseat] = useState();
  var listofseatselected = [];
  useEffect(() => {
    fetch("http://localhost:2000/seats")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setSeats(data);
      });
  }, []);

  const handleSubmit = (e) => {
    console.log(e.target.value);
    listofseatselected.push(e.target.value);
    console.log(listofseatselected);
  };

  const handleSeatClick = (seat) => {
    if (seat.occupy === "no") {
      setSeats((seats) =>
        seats.map((s) =>
          s.id === seat.id ? { ...s, selected: !s.selected } : s
        )
      );
      if (!seat.selected) {
        listofseatselected.push(seat.id);
        console.log("Selected seats: ", listofseatselected);
      }
    }
  };
  const renderSeat = (seat) => {
    let style;
    if (seat.occupy === "yes") {
      style = { backgroundColor: "white" };
    } else if (seat.selected) {
      style = { backgroundColor: "cyan" };
    } else {
      style = { backgroundColor: "grey" };
    }
    return (
      <div
        key={seat.id}
        style={{ ...style, width: "20px", height: "20px" }}
        onClick={() => handleSeatClick(seat)}
      >
        {seat.occupy === "no" && !seat.selected ? seat.seat : ""}
      </div>
    );
  };
  const filterSeats = (seatIds) => {
    return seats.filter((seat) => seatIds.includes(seat.id));
  };
  const renderRow = (rowSeats) => {
    return (
      <tr>
        {rowSeats.map((seat) => (
          <td key={seat.id}>{renderSeat(seat)}</td>
        ))}
      </tr>
    );
  };
  const renderSelectedSeats = () => {
    return (
      <table>
        <thead>
          <tr>
            <th>Seat</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {selectedSeats.map((seat) => (
            <tr key={seat.id}>
              <td>{seat.seat}</td>
              <td>{seat.selected ? "Selected" : "Not selected"}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  };
  const firstRowSeats = seats.filter((seat) => [1, 2, 3].includes(seat.id));
  const secondRowSeats = seats.filter((seat) => [4, 5, 6].includes(seat.id));
  const thirdRowSeats = seats.filter((seat) => [7, 8, 9].includes(seat.id));
  return (
    <div>
      <p>Select your seats</p>
      {seats.length > 0 && (
        <div>
          <table>
            <tbody>
              {renderRow(firstRowSeats)}
              {renderRow(secondRowSeats)}
              {renderRow(thirdRowSeats)}
            </tbody>
          </table>
          <p>Selected seats</p>
          {selectedSeats.length > 0 ? (
            renderSelectedSeats()
          ) : (
            <p>No seats selected yet</p>
          )}
        </div>
      )}
    </div>
  );
};

export default SelectSeats;
