import React from 'react';

import { Link, useNavigate, useLocation } from 'react-router-dom';



const NavBarUser = () => {

    const location = useLocation();
    const username = location.state.username;
    const history = useNavigate();

    const handleSubmit = () => {
        console.log("redirect to food and drink page");
        history('/foodanddrink', {state:{username}});
    }    
    
    return (
    <>
    
        <nav>
            
            <div className='menuitem'>
            

                    <Link to='/user'>ABC Cinema</Link>   
                    
        
            </div>
            <div className='menuitem_login'>
                    {/* <Link to='/loyaltytransaction' onClick={handleSubmit}>Transaction History</Link> */}
                    <button onClick={handleSubmit}>Transaction History</button>
                    <h3>{username}</h3>
                    <Link to='/reviewrating' >Review & Rating</Link>

                    
                    

                    <Link to='/loyaltypage'>Loyalty Point</Link>
                    <Link to='/'>Logout</Link>
        
            </div>

        </nav>
    </>
    )
}

export default NavBarUser
