import React, { useState } from 'react';
import { createSearchParams, Link, useNavigate } from 'react-router-dom';
import '../w3.css';
import NavBar from './NavBar';
import Modal from 'react-modal';

// Entity component
const User = ({ username, password, status, acctype, loyaltypoint, seatpref, id }) => {
  return {
    username,
    password,
    status,
    acctype,
    loyaltypoint,
    seatpref,
    id,
  };
};

// Controller component
const UserController = () => {
  const [modalIsOpen4, setModalIsOpen4] = useState(false);
  const history = useNavigate();

  const handleSubmit = (e, username, password) => {
    e.preventDefault();
    fetch('http://localhost:8005/user')
      .then(res => {
        return res.json();
      })
      .then(data => {
        console.log(data);
        const user = data.find(
          record => record.username === username && record.password === password && record.status === 'Active'
        );
        if (user) {
          const { acctype, loyaltypoint, seatpref, id } = user;
          if (acctype === 'customer') {
            console.log('redirect to customer page');
            history('/user', { state: { username, loyaltypoint, seatpref, id } });
          } else if (acctype === 'staff') {
            console.log('redirect to staff page');
            history('/stafffoodanddrink', { state: { username } });
          } else if (acctype === 'Manager') {
            console.log('redirect to manager page');
            history('/manager', { state: { username } });
          } else if (acctype === 'System Admin') {
            console.log('redirect to admin page');
            history('/admin', { state: { username } });
          }
        } else {
          setModalIsOpen4(true);
        }
      });
  };

  return {
    handleSubmit,
    modalIsOpen4,
    setModalIsOpen4,
  };
};

// Boundary component
const Login = () => {
    const [username, setusername] = useState('');
    const [password, setpassword] = useState('');
    const { handleSubmit, modalIsOpen4, setModalIsOpen4 } = UserController();
    const user = User({ username, password });
  
    const handleSubmit2 = () => {
      setModalIsOpen4(false);
    };
  
    return (
      <>
        <NavBar />
        <div className="w3-content w3-padding w3-center" style={{ width: '60%' }}>
          <div className="w3-container w3-margin-top" id="login">
            <div className="w3-padding-32">
              <h1 className="w3-border-bottom w3-border-teal w3-padding-64">Sign In</h1>
              <form onSubmit={e => handleSubmit(e, user.username, user.password)} className="w3-padding-32">
                <input
                  value={user.username}
                  placeholder="Username"
                  onChange={e => setusername(e.target.value)}
                  className="w3-input w3-section w3-border w3-round"
                  type="text"
                  required
                  name="Username"
                />
                <input
                  type="password"
                  value={user.password}
                  placeholder="Password"
                  onChange={e => setpassword(e.target.value)}
                  className="w3-input w3-section w3-border w3-round"
                  required
                  name="Password"
                />
                <div className="w3-container w3-padding-32">
                    <button type="submit" className="w3-button w3-teal w3-round w3-margin-top">
                    Sign In
                    </button>
                </div>
                </form>
                <div className="w3-container">
                <p>
                    Don't have an account?{' '}
                    <a href="/signup" className="w3-text-teal">
                    Sign Up here
                    </a>
                </p>
                </div>
                </div>
            </div>
        </div>
        {/* Boundary */}
        <Modal
            isOpen={modalIsOpen4}
            onRequestClose={() => setModalIsOpen4(false)}
            contentLabel="Login Success"
            className="Modal"
            overlayClassName="Overlay"
        >
            <div>
            <h1>Login Successful!</h1>
            <button onClick={handleSubmit2} className="w3-button w3-round w3-teal">
                Okay
            </button>
            </div>
        </Modal>
        </>
        );
    };
    
    export default Login;
