import { useState, useEffect } from "react";
const FoodAndDrink = () => {
    const [foodanddrink, setfoodanddrink] = useState([]);
    const [check, setCheck] = useState(false);
    const [quantity, setquantity] = useState();
    useEffect(()=>{
        fetch('http://localhost:9000/foodanddrink')
        .then(res =>{
            return res.json();
        })
        .then(data => {
            console.log(data);
            setfoodanddrink(data);
            console.log(data);
        });
    },[]);
    const handleChange = (e) =>{
        setCheck(e.target.checked);
    }
    return ( 
        <div>
            <h1>Food And Drink Purchase Section</h1>
            <p>Select list of items to purchase:</p>
            <form>
                <table>
                    <tr>
                        
                        <th>Item name</th>
                        <th>Item Description:</th>
                        <th>Price</th>
                        <th>Submit Button</th>
                    </tr>
                    {foodanddrink.map(record =>(
                        <tr id={record.key}>
                            <td>{record.itemname}</td>
                            <td>{record.itemdesc}</td>
                            <td>${record.price.toFixed(2)}</td>
                            <td><button>Choose this</button></td>
                        </tr>
                    ))}
                </table>
                <label>Item Quantity: </label>
                <select value={quantity} onChange={(e)=>setquantity(e.target.value)}>
                    <option>1</option>
                    <option>2</option>
                    <option>3</option>
                    <option>4</option>
                    <option>5</option>
                </select>
                <p>Total: $</p>
                <button>Confirm Order</button>
            </form>
        </div>
     );
}
 
export default FoodAndDrink;
