import { useEffect, useState } from "react";
import NavBarUser from "./NavBarUser";
import { useLocation } from 'react-router-dom';
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";

const SelectSeats = () => {
  const [data2, setData2] = useState([]);
  const [seats, setSeats] = useState([]);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [tickettype, setTickettype] = useState();

  const location = useLocation();
  const history = useNavigate();
  const username = location.state.username;
  var loyaltypoint = location.state.loyaltypoint;
  var seatpref = location.state.seatpref;
  var id = location.state.id;

  var selectedTimeslot = location.state.selectedTimeslot;
  var selectedDate = location.state.selectedDate;
  var selectedMovie = location.state.selectedMovie;
  var selectedId = location.state.selectedId;

  useEffect(() => {
    fetch("http://localhost:8009/moviesession")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setData2(data);
        console.log("console 1");
        console.log(data);
      });

  }, []);

  const handleSomething = () => {
    console.log("console 2");
    console.log(data2);
    setSeats(data2.filter(filter => filter.id === selectedId).flatMap(session => session.seats));
    console.log("console 3");
    console.log(seats);
  };

  useEffect(() => {
    handleSomething();
  }, [data2]);

  const handleReset = () => {
    setSelectedSeats([]);
    setSeats((seats) =>
      seats.map((s) => {
        if (s.selected) {
          return { ...s, selected: false };
        }
        return s;
      })
    );
  };

  const handleSubmit = (e) => {
    console.log(e.target.value);
    console.log("Selected seats:", selectedSeats);
    const numberOfSelectedSeats = selectedSeats.length;
    console.log("number of selected seats: ", numberOfSelectedSeats);

    //const updatedSeats = selectedSeats;
    const movieSessionId = selectedId;
    console.log(movieSessionId)
    fetch(`http://localhost:8009/moviesession/${movieSessionId}`)
    .then(res=>{
      return res.json();
    })
    .then(data => {

      console.log(data)
      const updatedSeats = data.seats.map(seat => {
        if (selectedSeats.includes(seat.seat)) {
          return { seat: seat.seat, occupy: "yes", id: seat.id };
        } else {
          return seat;
        }
      });

      fetch(`http://localhost:8009/moviesession/${movieSessionId}`, 
      {
        method: "PUT",
        headers: {"Content-Type": "application/json"},
        body: JSON.stringify({...data, seats: updatedSeats})
      })
      .then(response => {
        if (response.ok) {
          // handle successful update
          console.log("update successfully")
        } else {
          throw new Error("Failed to update seats.");
        }
      })
      .catch(error => {
        // handle error
        console.log("something error")
      });

    })
    
  };

  const handleSeatClick = (seat) => {
    if (seat.occupy === "no") {
      setSeats((seats) =>
        seats.map((s) =>
          s.id === seat.id ? { ...s, selected: !s.selected } : s
        )
      );
      if (!seat.selected) {
        setSelectedSeats((selectedSeats) => [...selectedSeats, seat.seat]);
      } else {
        setSelectedSeats((selectedSeats) =>
          selectedSeats.filter((selectedSeat) => selectedSeat !== seat.seat)
        );
      }
    }
  };
  const renderSeat = (seat) => {
    let style;
    if (seat.occupy === "yes") {
      style = { backgroundColor: "white" };
    } else if (seat.selected) {
      style = { backgroundColor: "cyan" };
    } else {
      style = { backgroundColor: "grey" };
    }
    return (
      <div
        key={seat.id}
        style={{ ...style, width: "20px", height: "20px" }}
        onClick={() => handleSeatClick(seat)}
      >
        {seat.occupy === "no" && !seat.selected ? seat.seat : ""}
      </div>
    );
  };
  const filterSeats = (seatIds) => {
    return seats.filter((seat) => seatIds.includes(seat.id));
  };
  const renderRow = (rowSeats) => {
    return (
      <tr>
        {rowSeats.map((seat) => (
          <td key={seat.id}>{renderSeat(seat)}</td>
        ))}
      </tr>
    );
  };

  const firstRowSeats = seats.filter((seat) => [1, 2, 3].includes(seat.id));
  const secondRowSeats = seats.filter((seat) => [4, 5, 6].includes(seat.id));
  const thirdRowSeats = seats.filter((seat) => [7, 8, 9].includes(seat.id));

  return (
    <>
    <NavBarUser/>

    <div>
      <p>{selectedTimeslot}</p>
      <p>{selectedDate}</p>
      <p>{selectedMovie}</p>
      <p>{selectedId}</p>

      <p>Select Ticket Type:</p>
      <select className="w3-input w3-section w3-border w3-round w3-dark-grey" value={tickettype} onChange={(e)=>setTickettype(e.target.value)}>
          <option value="">Ticket Type</option>
          <option value="adult">adult</option>
          <option value="child">child</option>
          <option value="student">student</option>
          <option value="senior">senior</option>
      </select>
      <p>{tickettype}</p>

      <p>-Screen-</p>
      <div>
        <table>
          <tbody>
            {renderRow(firstRowSeats)}
            {renderRow(secondRowSeats)}
            {renderRow(thirdRowSeats)}
          </tbody>
        </table>
      </div>
      <div>
        <p>Selected seats: {selectedSeats.join(", ")}</p>
        {/* <Link to={{ pathname: "/", query: { seats: selectedSeats } }}> */}
        <button onClick={handleReset}>Reset</button>
        <button onClick={handleSubmit}>Submit</button>
        {/* </Link> */}
      </div>
      
    </div>

    </>
  );
      
};

export default SelectSeats;
