import NavBarStaff from './NavBarStaff';
import React, { useEffect, useState } from 'react';
import AdoptList from './AdoptList';
import ReleaseList from './ReleaseList';
import ViewSession from './viewSession';
import { useLocation, useNavigate } from 'react-router-dom';


const Staff = () => {
    const history = useNavigate();
    const [foodanddrink, setfoodanddrink] = useState([]);
    const [quantity, setquantity] = useState();
    const [item, setItem]= useState();
    const [itemstatus, setitemstatus] = useState("Unpaid");
    const [isPending, setIsPending] = useState(false);
    const [price, setPrice] = useState();
    var totalamount = quantity * price;
    
    useEffect(()=>{
        fetch('http://localhost:9000/foodanddrink')
        .then(res =>{
            return res.json();
        })
        .then(data => {
            setfoodanddrink(data);
        });
    },[]);

    const handleSubmit = (e) => {
        var customerid = "staff";
        const loyaltytransaction = {item, itemstatus, quantity, customerid};
        e.preventDefault();
        setIsPending(true);
        
        fetch('http://localhost:8003/loyaltytransaction',{
            method: 'POST',
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(loyaltytransaction)
        }).then(()=>{
            console.log("Order have been placed");
            history('/user', {state:{username, loyaltypoint, seatpref, id}});
        })
    }
    
    const tableStyles = {
        borderCollapse: 'collapse',
        width: '100%',
        backgroundColor: '#f9f9f9',
        color: '#444'
    }
    
    const tableHeaderStyles = {
        backgroundColor: '#333',
        color: '#fff',
        fontWeight: 'bold'
    }
    
    const tableDataStyles = {
        textAlign: 'left',
        padding: '8px'
    }

    const alternatingRowStyles = {
        backgroundColor: '#f2f2f2'
    }

    return (
        <>
        
        <NavBarStaff />

        

        

        { error && <div> {error} </div> }
        {isPending && <div>Loading...</div>}

        <div className="w3-container w3-padding-8">

            <div className="w3-row">    

                <div className="w3-half w3-padding-large">
                    <h3 className="w3-padding-32 w3-xxxlarge w3-border-bottom w3-border-light-grey">Adopt List</h3>
                    <div className='w3-padding-16'>
                    {adopt && <AdoptList adopt={adopt} title=" " />}
                    </div>
                    
                </div>

                <div className="w3-half w3-padding-large">
                    <h3 className="w3-padding-32 w3-xxxlarge w3-border-bottom w3-border-light-grey">Release List</h3>
                    <div className='w3-padding-16'>
                    {release && <ReleaseList release={release} title=" " />}
                    </div>
                    
                </div>

            </div>

        </div>

        </>
    )
}

export default Staff
