import { useEffect, useState } from "react";
const SelectSeats = () => {
  const [seats, setseats] = useState([]);
  const [seat, setseat] = useState();
  var listofseatselected = [];
  useEffect(() => {
    fetch("http://localhost:2000/seats")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setseats(data);
      });
  }, []);
  const handleSubmit = (e) => {
    console.log(e.target.value);
    listofseatselected.push(e.target.value);
    console.log(listofseatselected);
  };
  const cubegrey = {
    width: "20px",
    height: "20px",
    backgroundColor: "grey",
  };
  const cubewhite = {
    width: "20px",
    height: "20px",
    backgroundColor: "white",
  };
  const cubecyan = {
    width: "20px",
    height: "20px",
    backgroundColor: "cyan",
  };
  const renderSeat = (seat) => {
    const occupy = seat.occupy;
    let style;
    if (occupy === "yes") {
      style = cubecyan;
    } else {
      style = cubewhite;
    }
    return <div style={style} />;
  };
  return (
    <div>
      <table>
        <tr>
          <th></th>
          <th>1</th>
          <th>2</th>
          <th>3</th>
        </tr>
        <tr>
          <td>A</td>
          {seats
            .filter(
              (current) =>
                current.id === 1 || current.id === 2 || current.id === 3
            )
            .map((record) => (
              <td>
                <div key={record.id}>{renderSeat(seat)}</div>
              </td>
            ))}
        </tr>
        <tr>
          <td>B</td>
          {seats
            .filter(
              (current) =>
                current.id === 4 || current.id === 5 || current.id === 6
            )
            .map((record) => (
              <td>
                <img value={record.id} src={record.imgsrc}></img>
              </td>
            ))}
        </tr>
        <tr>
          <td>C</td>
          {seats
            .filter(
              (current) =>
                current.id === 7 || current.id === 8 || current.id === 9
            )
            .map((record) => (
              <td>
                <img value={record.id} src={record.imgsrc}></img>
              </td>
            ))}
        </tr>
        {seats
          .filter((current) => current.occupy === "no")
          .map((record) => (
            <button value={record.seat} onClick={handleSubmit}>
              {record.seat}
            </button>
          ))}
        <button>Confirm Booking</button>
      </table>
      <p>{}</p>
    </div>
  );
};

export default SelectSeats;
