import React from "react";
import { useEffect, useState } from "react";

function GenerateReport() {
  const [show, setShow] = useState("Weekly")
  const [sales ,setSales] = useState();

  const apiUrl_tx = process.env.REACT_APP_API_URL_TX;

  useEffect(() => { //load data on page load 
    fetch(`${apiUrl_tx}/transaction`)
    .then(response => response.json())
    .then(data => {
         setSales(data);
    })
    .catch(error => console.error(error));
  }, []);
  //need transaction 
  return (
     <>
     <div className="text-white d-flex-column">
          <div>
            <button onClick={() => setShow("Weekly")}>Weekly sales</button>
            <button onClick={() => setShow("Monthy")}>Monthly sales</button>
          </div>
          <div>
            {
              show === "Weekly" ? (
                <>
                {
                  sales?.map((item) => (
                    <>
                      <p>{item.price}</p>
                    </>
                  ))
                }
                </>
              ) 
              : (
                <>monthly</>
              )
            }
          </div>
     </div>
     </>
  );
}

export default GenerateReport;
