import { useEffect, useState } from "react";
const SelectSeats = () => {
  const [seats, setseats] = useState([]);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [seat, setseat] = useState();
  var listofseatselected = [];
  useEffect(() => {
    fetch("http://localhost:2000/seats")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setseats(data);
      });
  }, []);

  const handleSeatSelection = (selectedSeat) => {
    // Check if the seat is not already occupied
    if (selectedSeat.occupy === "no") {
      // Add the seat to the list of selected seats
      setSelectedSeats([...selectedSeats, selectedSeat]);

      // Change the seat color to cyan
      selectedSeat.cubecyan;
      renderSeat(selectedSeat);
    }
  };
  const handleSubmit = (e) => {
    console.log(e.target.value);
    listofseatselected.push(e.target.value);
    console.log(listofseatselected);
  };
  const cubegrey = {
    width: "20px",
    height: "20px",
    backgroundColor: "grey",
  };
  const cubewhite = {
    width: "20px",
    height: "20px",
    backgroundColor: "white",
  };
  const cubecyan = {
    width: "20px",
    height: "20px",
    backgroundColor: "cyan",
  };
  const renderSeat = (seat) => {
    const occupy = seat.occupy;
    let style;
    if (occupy === "yes") {
      style = cubewhite;
    } else {
      style = cubecyan;
    }
    return <div style={style} />;
  };
  return (
    <div>
      {seats.map((seat) => (
        <div key={seat.id}>{renderSeat(seat)}</div>
      ))}
    </div>
  );
};

export default SelectSeats;
