import { useEffect, useState } from "react";
const SelectSeats = () => {
  const [seats, setSeats] = useState([]);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [seat, setseat] = useState();
  var listofseatselected = [];
  useEffect(() => {
    fetch("http://localhost:2000/seats")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setSeats(data);
      });
  }, []);

  const handleSubmit = (e) => {
    console.log(e.target.value);
    listofseatselected.push(e.target.value);
    console.log(listofseatselected);
  };
  const cubegrey = {
    width: "20px",
    height: "20px",
    backgroundColor: "grey",
  };
  const cubewhite = {
    width: "20px",
    height: "20px",
    backgroundColor: "white",
  };
  const cubecyan = {
    width: "20px",
    height: "20px",
    backgroundColor: "cyan",
  };
  const handleSeatClick = (seat) => {
    if (seat.occupy === "no") {
      setSeats((seats) =>
        seats.map((s) =>
          s.id === seat.id ? { ...s, selected: !s.selected } : s
        )
      );
      if (!seat.selected) {
        listofseatselected.push(seat.id);
        console.log("Selected seats: ", listofseatselected);
      }
    }
  };
  const renderSeat = (seat) => {
    let style;
    if (seat.occupy === "yes") {
      style = { backgroundColor: "white" };
    } else if (seat.selected) {
      style = { backgroundColor: "cyan" };
    } else {
      style = { backgroundColor: "grey" };
    }
    return (
      <div
        key={seat.id}
        style={{ ...style, width: "20px", height: "20px" }}
        onClick={() => handleSeatClick(seat)}
      >
        {seat.occupy === "no" && !seat.selected ? seat.seat : ""}
      </div>
    );
  };
  const filterSeats = (seatIds) => {
    return seats.filter((seat) => seatIds.includes(seat.id));
  };
  const renderRow = (seatIds) => {
    const filteredSeats = filterSeats(seatIds);
    return (
      <tr>
        {filteredSeats.map((seat) => (
          <td key={seat.id}>{renderSeat(seat)}</td>
        ))}
      </tr>
    );
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Type</th>
            <th>Quantity</th>
            <th>Amount</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>
              {filterSeats([1, 2, 3])
                .map((seat) => seat.selectedSeats)
                .join(" ")}
            </td>
            <td>{filterSeats([1, 2, 3]).length}</td>
            <td>{filterSeats([1, 2, 3]).length * 10}</td>
          </tr>
          <tr>
            <td>
              {filterSeats([4, 5, 6])
                .map((seat) => seat.selectedSeats)
                .join(" ")}
            </td>
            <td>{filterSeats([4, 5, 6]).length}</td>
            <td>{filterSeats([4, 5, 6]).length * 10}</td>
          </tr>
          <tr>
            <td>
              {filterSeats([7, 8, 9])
                .map((seat) => seat.seat)
                .join(" ")}
            </td>
            <td>{filterSeats([7, 8, 9]).length}</td>
            <td>{filterSeats([7, 8, 9]).length * 10}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default SelectSeats;
